package javaClass.JavaCore;

public class breakAndContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i>=0;i++) {
			if(i==10) {
				System.out.println("This is the limit to stop");
				break;
			}
			else {
				System.out.println(i);
			}
	
		}

	}

}
